
public class Charactertype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char c = 'A';
		System.out.println(c);
		
		c = 65; //implicit typecasting
		System.out.println(c);
		
		c = '\u0041';
		System.out.println(c);
		
		c = '\u0c05';
		System.out.println(c);
	}
	}


